<script setup lang="ts">
import {onMounted, ref,getCurrentInstance} from "vue";
const Instance = getCurrentInstance()
const echarts = Instance?.appContext.config.globalProperties.$echarts
const domRef = ref(null)
let smallNodeSize = 30;
let middleNodeSize = 60
let bigNodeSize = 100;
let bg = `data:image/png;base64,`
let bg2 = `data:image/png;base64,`
const option = {
  tooltip: {
    show: false
  },
  title:{
    text:'成绩组成',
    textStyle:{
      color:'#fff'
    }
  },
  color: ['rgba(255,255,255,0)'],
  backgroundColor: '#000',
  series: [
    {
      type: 'graph',
      layout: "force",
      draggable: true,
      force: {
        initLayout: "circular",
        repulsion: 3.5 * bigNodeSize,
        edgeLength: bigNodeSize
      },
      itemStyle: {
        normal: {
          label: {
            show: true
          }
        },
      },
      nodes: [
        // 球一
        {
          id: '1-1',
          name: '总成绩',
          label: '球1',
          symbolSize: bigNodeSize,
        },
        {
          id: '1-2',
          name: '期末成绩',
          label: '球1-1',
          symbolSize: middleNodeSize,
        },
        {
          id: '1-3',
          name: '平时成绩',
          label: '球1-2',
          symbolSize: middleNodeSize,
        },
        // 球二
        {
          id: '1-4',
          name: '考勤成绩',
          label: '球2',
          symbolSize: smallNodeSize,
        },
        {
          id: '1-5',
          name: '作业成绩',
          label: '球2-1',
          symbolSize: smallNodeSize,
        },
        {
          id: '1-6',
          name: '期中成绩',
          label: '球2-2',
          symbolSize: smallNodeSize,
        },
      ],
      links: [
        // 球一开始
        {
          source: '1-1',
          target: '1-2',
        },
        {
          source: '1-1',
          target: '1-3'
        },
        {
          source: '1-3',
          target: '1-4'
        },
        // 球二开始
        {
          source: '1-3',
          target: '1-5'
        },
        {
          source: '1-3',
          target: '1-6'
        },
      ]
    }
  ]
}

// 隐藏连接线
option.series[0].links.forEach((link) => {
  //@ts-ignore
  link['lineStyle'] = {
    width: 5,

    color:'#fff'
  }
})
// 大球 的symbol设置 以及样式其它。。
option.series[0].nodes.forEach((node) => {
  //@ts-ignore
  if (node.id.split('-')[1] == 0) {
    //@ts-ignore
    node['symbol'] = 'image://' + bg;
    //@ts-ignore
    node['label'] = {
      show: true,
      // textShadowColor: '#75ADFB',
      // textShadowBlur: 20,
      fontSize: 20,
      color: '#75ADFB',
      //@ts-ignore
      formatter: function (formatterParam) {
        let name_ = formatterParam.data.name;
        return name_;
      }
    }
  } else {
    //@ts-ignore
    node['symbol'] = 'image://' + bg2;
  }
})

let active = '1-0';
// 透明度隐藏有问题的；拖拽会 有位置占用
// 可以使用filter过滤掉
let clickNodesFun = () => {
  option.series[0].nodes.forEach((node) => {
    //@ts-ignore
    if (node.id.split('-')[1] != 0) {
      //@ts-ignore
      node['itemStyle'] = {
        opacity: 0,
      }
      // 小球label 样式
      //@ts-ignore
      node['label'] = {
        show: true,
        //@ts-ignore
        formatter: function (formatterParam) {
          let name_ = formatterParam.data.name;
          return `{a|${name_}}\n{b|${name_}}`;
        },
        //偏移 由于加了 \n换行（不加rich宽高设置完毕不会换行）宽高区域没有顶在球的顶部有偏差；移动10像素
        offset: [0, 17],      //rich b的高度/2
        rich: {
          a: {
            color: '#75ADFB',
            height: smallNodeSize,
            width: smallNodeSize,
            lineHeight: smallNodeSize,
            align: 'center',
            fontSize: 30,

            // textShadowColor: 'red',
            // textShadowBlur: 20,
          },
          b: {
            width: smallNodeSize,
            height: 30,
            align: 'center',
            lineHeight: 30,
            fontSize: 20,
            color: "#fff"
          }
        }
      }

    }
    let active_split = active.split('-')
    let active_1 = active_split[0];
    let active_2 = active_split[1];
    let nodeId_split = node.id.split('-');
    let nodeId_1 = nodeId_split[0];
    let nodeId_2 = nodeId_split[1];
    if (active_1 == nodeId_1) {
      //@ts-ignore
      node['itemStyle'] = {
        opacity: 1,
        borderWidth: 2,
        borderColor: '#75ADFB'
      }
    }
  })
}
onMounted(()=>{
  let myChart = echarts.init(domRef.value)
  myChart.setOption(option)
})
</script>

<template>
<div style="width: 100%;height: 100%" ref="domRef"></div>
</template>

